Graphics.prototype.setFontBoxyBold = function(scale) {
  // Actual height 16 (15 - 0)
  this.setFontCustom( E.toString(require('heatshrink').decompress(atob(''))),
    32,
    atob("BggKEg4UEgoKCgwQCgwIDAwMDg4ODg4ODg4ICAwNDBAQDg4ODg4ODg4MDg4OEhAODhAODhAODhIOEA4KDAo="),
    16+(scale<<8)+(1<<16)
  );
  return this;
};

// TODO add justify aka g.setFontAlign
exports.render = function(text, x, y) {
  g.setFontBoxyBold();
  g.setColor(1,1,1);
  g.setBgColor(0,0,0);
  g.drawString(text.toUpperCase(), x, y, true);
};
