// generated using https://www.espruino.com/Font+Converter
// from https://opengameart.org/content/boxy-bold-truetype-font
Graphics.prototype.setFontBoxyBoldGround = function(scale) {
  // Actual height 16 (15 - 0)
  this.setFontCustom(
    E.toString(require('heatshrink').decompress(atob(''))),
    32,
    atob("BggKEg4UEgoKCgwQCgwIDAwMDg4ODg4ODg4ICAwNDBAQDg4ODg4ODg4MDg4OEhAODhAODhAODhIOEA4KDAoQDAoODgwODgwODgoMDgoSDg4ODg4ODA4OEg4ODgoIChII"),
    16+(scale<<8)+(1<<16)
  );
  return this;
};

// edited from the original in https://www.glyphrstudio.com/online/
Graphics.prototype.setFontBoxyBoldFigure = function(scale) {
  // Actual height 12 (11 - 0)
  this.setFontCustom(
    E.toString(require('heatshrink').decompress(atob(''))),
    32,
    atob("BggKEg4UEgoKCgwQCgwIDAwMDg4ODg4ODg4ICAwNDBAQDg4ODg4ODg4MDg4OEhAODhAODhAODhIOEA4KDAoQDAoODgwODgwODgoMDgoSDg4ODg4ODA4OEg4ODgoIChII"),
    16+(scale<<8)+(1<<16)
  );
  return this;
};




// TODO add justify aka g.setFontAlign
exports.render = function(text, x, y) {
  g.setFontBoxyBoldGround(1)
    .setColor(1,1,1)
    .drawString(text.toUpperCase(), x, y)
    .setColor(0,0,0)
    .setFontBoxyBoldFigure(1)
    .drawString(text.toUpperCase(), x, y);
};
